mapper.update:
update T_DECORATE_DETAIL
set DECORATION_APPLY_ID = #{decorationApplyId,jdbcType=DECIMAL},
DETAIL_ORDER = #{detailOrder,jdbcType=DECIMAL},
DETAIL_CONTENT = #{detailContent,jdbcType=OTHER}
where DECORATE_DETAIL_ID = #{decorateDetailId,jdbcType=DECIMAL}

mapper.select:
select DECORATE_DETAIL_ID, DECORATION_APPLY_ID, DETAIL_ORDER, DETAIL_CONTENT
from T_DECORATE_DETAIL
where DECORATE_DETAIL_ID = #{decorateDetailId,jdbcType=DECIMAL}
