mapper.update:
UPDATE T_PROPERTY_FEE_PAY_DETAILS
SET STATUS = 'Y'
WHERE PROPERTY_FEE_PAY_DETAILS_ID = #{id}

sql.where:
AND HOUSE_INFO_ID IN (select "pk_house" from T_HOUSE_INFO_DETAILS WHERE "pk_project"=#{projectId})

    AND PAY_TIME >= startDate AND PAY_TIME <= endDate


    AND FEETYPE=#{feeType}


    AND PAY_MANNER=#{payManner}
